import React, { useState, useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'

import Set from './Set'

const Exercise = ({plan, workoutDay, name}) => {
    const [sets, setSets] = useState([])
    const [sumReps, setSumReps] = useState()

    const day = plan.days.find(day => day.date === workoutDay)
    const exercise = day.exercises.find(exercise => exercise.name === name)


    const passback = async (reps, isComplete, i) => {
        setSets([...sets.filter(set => set.i !== i), {
            reps: reps, 
            isComplete: isComplete, 
            i: i
        }])
    }

    const updatePlan = () => {
        let newExercise = {
            name: exercise.name, 
            sets: exercise.sets,
            reps: exercise.reps, 
            weight: exercise.weight, 
            complete: exercise.complete
        }
        if (sumReps > 30) {
            newExercise.sets += 1
        }

        console.log(newExercise)


    }

    useEffect(() => {
        repAdd(sets)
    }, [sets])

    useEffect(() => {
        updatePlan()
    }, [sumReps])

    const repAdd = (sets) => {
        let sum = 0
        sets.forEach(set => {
            sum+=set.reps
        })
        setSumReps(sum)
    }

    const isEnabled = (j) => {
        return(
        j === 0 ? true : (sets.filter(set => set.i === j-1)[0] ? (sets.filter(set => set.i === j-1)[0].isComplete) : false)
        )
    }

    const renderSets = (sets, reps) => {
        let row = []
        for (let i = 0; i < sets; i++){
            row.push(
                <Set i={i} startingReps={reps} passback={passback} enabled={isEnabled(i)}/>
            )
        }
        return <Row className="my-3 justify-content-center">{row}</Row>
    } 

    return (
        <>
            <Row className="my-3">{exercise.name}</Row>
            {renderSets(exercise.sets, exercise.reps)}
            {JSON.stringify(sets)}<br />
            {sumReps}<br />
            </>
    )
}

export default Exercise
