import React, {useState, useEffect, useRef} from 'react'
import { Col } from 'react-bootstrap'

const Set = ({startingReps, i, passback}) => {
    const [isComplete, setIsComplete] = useState(false);
    const [reps, setReps] = useState(startingReps)

    const loaded = useRef(false)
    useEffect(() => {
        if (loaded.current) {
        passback(reps, isComplete, i)
        console.log('updated')
        } else {
            loaded.current = true
        }
    }, [reps, isComplete])

    const handleClick = async () => {
            if (!isComplete){
            setIsComplete(true)
        } else if (reps > 1) {
            setReps(reps-1)
        } else {
            setIsComplete(false)
            setReps(startingReps)
        }
    }

    return (
        <Col key={i} className="text-center"><div className={`set mx-auto ${isComplete ? 'complete' : ''}`} onClick={() => {
            handleClick()
        }}>{reps}</div></Col>
    )
}

export default Set
