import React, { useState, useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'
import moment from 'moment'

import Set from './Set'

const Exercise = ({plan, workoutDay, name, refreshPlan}) => {

    const day = plan.days.find(day => day.date === workoutDay)
    const nextWeek = plan.days.find(day => day.date === moment(workoutDay).add(7, 'days').format('YYYY-MM-DD'))
    const nextWeekIndex = plan.days.findIndex(day => day.date === moment(workoutDay).add(7, 'days').format('YYYY-MM-DD'))
    const exercise = day.exercises.find(exercise => exercise.name === name)
    const nextWeekExercise = nextWeek ? nextWeek.exercises.find(exercise => exercise.name === name) : ''
    const nextWeekExerciseIndex = nextWeek ? nextWeek.exercises.findIndex(exercise => exercise.name == name) : ''

    const [sets, setSets] = useState(exercise.sets)
    const [sumReps, setSumReps] = useState()

    const passback = (completeReps, isComplete, i) => {
        const newSets = sets
        newSets[i] = {
            reps: completeReps, 
            complete: isComplete
        }
        setSets(newSets)
        console.log(sets)
    }

    const updatePlan = () => {
        if(!nextWeek){
            return
        } else {
        let newExercise = {
            name: nextWeekExercise.name, 
            sets: nextWeekExercise.sets,
            weight: nextWeekExercise.weight, 
        }
        if (sumReps > 30 && nextWeekExercise.sets.length === sets.length) {
            newExercise.sets.push({
                reps: nextWeekExercise.sets[0].reps, 
                complete: false
            })
        }

        let newDay = {
            name : nextWeek.name, 
            date: nextWeek.date, 
            exercises: nextWeek.exercises
        }

        newDay.exercises[nextWeekExerciseIndex] = newExercise

        let newPlan = {
            _id: plan._id, 
            name: plan.name,
            startDate: plan.startDate, 
            user: plan.user,
            days: plan.days
        }

        newPlan.days[nextWeekIndex] = newDay

        refreshPlan(newPlan)
        }
    }

    useEffect(() => {
        updatePlan()
    }, [sumReps])

    const repAdd = () => {
        let sum = 0
        sets.forEach(set => {
            set.complete ? sum+=set.reps : sum=sum
        })
        setSumReps(sum)
        console.log(sum)
        console.log(sumReps)
    }

    const isEnabled = (j) => {
        return(
        j === 0 ? true : (sets[j-1] ? (sets[j-1].complete) : false)
        )
    }

    const renderSets = (sets) => {
        let row = []
        for (let i = 0; i < sets.length; i++){
            row.push(
                <Set i={i} startingReps={sets[i].reps} complete={sets[i].complete} passback={passback} enabled={isEnabled(i)} repAdd={repAdd}/>
            )
        }
        return <Row className="my-3 justify-content-center">{row}</Row>
    } 

    return (
        <>
            <Row className="my-3">{exercise.name}</Row>
            {renderSets(exercise.sets)}
            {JSON.stringify(sets)}<br />
            {sumReps}<br />
            </>
    )
}

export default Exercise
