import React from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import { FaChevronLeft } from 'react-icons/fa'

const DailyWorkout = ({dailyWorkout, closeDailyWorkout}) => {

    const renderSets = (sets, reps) => {
        let row = []
        for (let i = 0; i < sets; i++){
            row.push(
                <Col>{reps}</Col>
            )
        }
        return <Row className="my-3 justify-content-center">{row}</Row>
    } 

    return (
        <Container>
        <Row className="my-3">
            <Col><FaChevronLeft onClick={closeDailyWorkout}/></Col>
            <Col>{dailyWorkout.name}</Col>
        </Row>
        {dailyWorkout.exercises.map(exercise => (
            <>
            <Row className="my-3">{exercise.name}</Row>
            {renderSets(exercise.sets, exercise.reps)}
            </>
        ))}
        </Container>
    )
}
export default DailyWorkout
