import React, { useState, useEffect }  from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import { FaChevronLeft } from 'react-icons/fa'
import Exercise from './Exercise'

const DailyWorkout = ({dailyWorkout, closeDailyWorkout, nextWeek, updatePlan}) => {
    const [nextWeekWorkout, setNextWeekWorkout] = useState(nextWeek ? nextWeek : '')

    const updateNextWeekExercise = (sumReps, name, prevSets) => {
        if (!nextWeek){
            return
        }
        else {
            let exercise = nextWeek.exercises.find(ex => ex.name === name);
            let index = nextWeek.exercises.findIndex(ex => ex.name === name);
            sumReps > 30 ? exercise.sets = prevSets + 1 : exercise.sets = prevSets
            nextWeek[index] = exercise
            setNextWeekWorkout(nextWeek)
            console.log(nextWeekWorkout)
        }
    }

    return (
        <Container>
        <Row className="my-3">
            <Col><FaChevronLeft style={{cursor: 'pointer'}} onClick={closeDailyWorkout}/></Col>
            <Col>{dailyWorkout.name}</Col>
        </Row>
        {dailyWorkout.exercises.map(exercise => (
            <Exercise exercise={exercise} nextWeekExercise={nextWeek ? nextWeek.exercises.find(ex => ex.name === exercise.name) : ''} updateNextWeekExercise={updateNextWeekExercise}/>
        ))}
        {nextWeekWorkout.exercises[0].reps}
        </Container>
    )
}
export default DailyWorkout
