import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import axios from 'axios'
import moment from 'moment'

import DailyWorkout from './DailyWorkout'
import PlanForm from './PlanForm'

const Workout = ({user}) => {
    const [plan, setPlan] = useState()
    const [dailyWorkout, setDailyWorkout] = useState()

    useEffect(() => {
        axios.get('/plans').then(res => {
            let userPlan = res.data.filter(plan => plan.user === user.username)
            console.log(userPlan)
            if(userPlan.length > 0) setPlan(userPlan[0])
        })
    }, [])

    const passUpPlan = (plan) => {
        setPlan(plan)
    }

    const closeDailyWorkout = () => {
        setDailyWorkout('')
    }

    const renderWeeks = () => {
        let startDate = moment(plan.startDate).add(12, 'hours')
        console.log(startDate.format(''))
        console.log(moment(startDate).format('d'))
        let rows =[]
        rows.push(<Row className="my-3 justify-content-center text-center"><Col>Mesocycle 1</Col></Row>)

        const daysRow = () => {
            let row = []
            for (let i = 0; i < 7; i++){
                row.push(<Col key={i}>{moment(startDate).startOf('week').add(i, 'days').format('dd')}</Col>)
            }
            return (<Row className="my-3 justify-content-center text-center">{row}</Row>)
        }

        rows.push(daysRow())

        for (let i = 0; i < 5; i ++){
            let sundayOfWeek = moment(startDate).format('d') === 0 ? moment(startDate) : moment(startDate).startOf('week').add(i*7, 'days')
            let weekRow = []
            for (let j = 0; j < 7; j++){
                let workoutDay = moment(sundayOfWeek).add(j, 'days').format('YYYY-MM-DD')
                plan.days.filter(day => day.date === workoutDay).length > 0 ? 
                weekRow.push(<Col className="loaded-day" onClick = {() => {
                    setDailyWorkout(plan.days.filter(day => day.date === workoutDay)[0])
                }}>{moment(workoutDay).format('MM/DD')}</Col>) : 
                weekRow.push(<Col>{moment(workoutDay).format('MM/DD')}</Col>)
            }
            rows.push(<Row className="my-3 justify-content-center text-center">{weekRow}</Row>)
            weekRow = []
        }
        return (rows)
    }

    return (
        <>{plan?
            <>
            {dailyWorkout ?
            <DailyWorkout dailyWorkout={dailyWorkout} closeDailyWorkout={closeDailyWorkout} /> : 
            renderWeeks()}
            </>:
            <PlanForm user={user} passUpPlan={passUpPlan} />}
        </>
    )
}

export default Workout
