import React, { useState, useEffect } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import axios from 'axios'
import moment from 'moment'

import PlanForm from './PlanForm'

const Workout = ({user}) => {
    const [plan, setPlan] = useState()

    useEffect(() => {
        axios.get('/plans').then(res => {
            let userPlan = res.data.filter(plan => plan.user === user.username)
            console.log(userPlan)
            if(userPlan.length > 0) setPlan(userPlan)
        })
    }, [])

    const passUpPlan = (plan) => {
        setPlan(plan)
    }

    const renderWeeks = () => {
        let startDate = plan.startDate
        let rows =[]
        rows.push(<Row className="justify-content-center text-center"><Col>Mesocycle 1</Col></Row>)

        const daysRow = () => {
            let row = []
            for (let i = 0; i < 7; i++){
                row.push(<Col>{moment(startDate).startOf('week').add(i, 'days').format('dd')}</Col>)
            }
            return (<Row className="justify-content-center">{row}</Row>)
        }

        rows.push(daysRow())
        return (rows)
    }

    return (
        <>{plan?
            renderWeeks():
            <PlanForm user={user} passUpPlan={passUpPlan} />}
        </>
    )
}

export default Workout
