import React, { useState, useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'
import moment from 'moment'

import Set from './Set'

const Exercise = ({plan, workoutDay, name, refreshPlan}) => {

    const day = plan.days.find(day => day.date === workoutDay)
    const exercise = day.exercises.find(exercise => exercise.name === name)
    const exerciseIndex = day.exercises.findIndex(exercise => exercise.name === name)

    const [sets, setSets] = useState(exercise.sets)
    const [sumReps, setSumReps] = useState()

    const passback = (completeReps, isComplete, i) => {
        const newSets = sets
        newSets[i] = {
            reps: completeReps, 
            complete: isComplete
        }
        setSets(newSets)
        console.log(sets)
    }

    const updatePlan = () => {
        if (sumReps > sets.length * 10 && weeks[0].exercises[exerciseIndex].sets.length === sets.length) {
            let newPlan = plan
            newPlan.days.forEach(planDay => {
                if (planDay.number === day.number && moment(planDay.date) > moment(day.date)){
                    console.log(planDay)
                    planDay.exercises[exerciseIndex].push({
                        reps: sets[0].reps, 
                        complete: false
                    })
                }
            })
            console.log(newPlan)

        }

        // refreshPlan(newPlan)
    }

    useEffect(() => {
        updatePlan()
    }, [sumReps])

    const repAdd = () => {
        let sum = 0
        sets.forEach(set => {
            set.complete ? sum+=set.reps : sum=sum
        })
        setSumReps(sum)
        console.log(sum)
        console.log(sumReps)
    }

    const isEnabled = (j) => {
        return(
        j === 0 ? true : (sets[j-1] ? (sets[j-1].complete) : false)
        )
    }

    const renderSets = (sets) => {
        let row = []
        for (let i = 0; i < sets.length; i++){
            row.push(
                <Set i={i} startingReps={sets[i].reps} complete={sets[i].complete} passback={passback} enabled={isEnabled(i)} repAdd={repAdd}/>
            )
        }
        return <Row className="my-3 justify-content-center">{row}</Row>
    } 

    return (
        <>
            <Row className="my-3">{exercise.name}</Row>
            {renderSets(exercise.sets)}
            {JSON.stringify(sets)}<br />
            {sumReps}<br />
            </>
    )
}

export default Exercise
