import React, { useState } from 'react'
import { Row, Col } from 'react-bootstrap'

import Set from './Set'

const Exercise = ({exercise}) => {
    const [sets, setSets] = useState([])

    const passback = (reps, isComplete, i) => {
        setSets([...sets.filter(set => set.i !== i), {
            reps: reps, 
            isComplete: isComplete, 
            i: i
        }])
    }

    const renderSets = (sets, reps) => {
        let row = []
        for (let i = 0; i < sets; i++){
            row.push(
                <Set i={i} startingReps={reps} passback={passback} />
            )
        }
        return <Row className="my-3 justify-content-center">{row}</Row>
    } 

    return (
        <>
            <Row className="my-3">{exercise.name}</Row>
            {renderSets(exercise.sets, exercise.reps)}
            {JSON.stringify(sets)}
            </>
    )
}

export default Exercise
