import React, { useState } from 'react'
import { Form, Button } from 'react-bootstrap'
import moment from 'moment'
import axios from 'axios'

const PlanForm = ( { user, passUpPlan }) => {
    const [benchVal, setBenchVal] = useState('')
    const [squatVal, setSquatVal] = useState('')
    const [date, setDate] = useState('')
    const [days, setDays] = useState([])

    const handleCheckToggle = (val) => {
        if (!days.includes(val)){
            setDays([...days, val])
        } else {
            setDays(days.filter(day => day !== val))
        }

        console.log(days)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(benchVal, squatVal, date, days)
        let plan = {}
        plan.startDate = date
        plan.user = user.username
        plan.days = []

        //First Mesocycle
        let firstCycleWeeks = 5
        for (let i = 0; i < firstCycleWeeks; i++){
            const startOfWeek = moment(date).add(i*7, 'days')
            days.forEach((day, index) => {
                let name
                let exercises = []
                const dayDate = moment(startOfWeek).add(day, 'days')
                if(index === 0){
                    name = "Chest "+(i+1)
                    exercises = [
                        {
                            name: "Incline Dumbbell Press",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.35)/5)*5,
                            complete: false
                        },
                        {
                            name: "Dumbbell Overhead Extension",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.35)/5)*5,
                            complete: false
                        },
                        {
                            name: "Flat Bench Press",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.8)/5)*5,
                            complete: false
                        },
                        {
                            name: "1-Arm Dumbbell Row",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.4)/5)*5,
                            complete: false
                        },
                        {
                            name: "Pullup",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal)/5)*5,
                            complete: false
                        },
                        {
                            name: "Dumbbell Lateral Raise",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.075)/5)*5,
                            complete: false
                        }
                    ]
                } else if (index === 1){
                    name = "Quads "+(i+1)
                    exercises = [
                        {
                            name: "Close Stance Squat",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.8)/5)*5,
                            complete: false
                        },
                        {
                            name: "Front Squat",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.75)/5)*5,
                            complete: false
                        },
                        {
                            name: "Single Leg Curl",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.25)/5)*5,
                            complete: false
                        },
                        {
                            name: "Calf Raises",
                            reps: 12,
                            sets: 5, 
                            weight: Math.ceil((squatVal*0.7)/5)*5,
                            complete: false
                        },
                        {
                            name: "Cable Tricep Pushdown",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.7)/5)*5,
                            complete: false
                        },
                        {
                            name: "Seated Dumbbell Shoulder Press",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.25)/5)*5,
                            complete: false
                        }
                    ]
                } else if(index === 2){
                    name = "Back "+(i+1)
                    exercises = [
                        {
                            name: "Chin-up",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal)/5)*5,
                            complete: false
                        },
                        {
                            name: "Lat Pulldown",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.8)/5)*5,
                            complete: false
                        },
                        {
                            name: "Cable Row",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.9)/5)*5,
                            complete: false
                        },
                        {
                            name: "Rear Dumbbell Flye",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.075)/5)*5,
                            complete: false
                        },
                        {
                            name: "Incline Dumbbell Press",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.35)/5)*5,
                            complete: false
                        },
                        {
                            name: "Flat Bench Press",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.9)/5)*5,
                            complete: false
                        }
                    ]
                } else {
                    name = "Hams/Glutes "+(i+1)
                    exercises = [
                        {
                            name: "Dumbbell Walking Lunge",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.2)/5)*5,
                            complete: false
                        },
                        {
                            name: "Romanian Deadlift",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.8)/5)*5,
                            complete: false
                        },
                        {
                            name: "Hack Squat",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((squatVal*0.5)/5)*5,
                            complete: false
                        },
                        {
                            name: "Dumbbell Curl",
                            reps: 12,
                            sets: 3, 
                            weight: Math.ceil((benchVal*0.15)/5)*5,
                            complete: false
                        },
                        {
                            name: "Barbell Shrug",
                            reps: 12,
                            sets: 3,
                            weight: Math.ceil((benchVal*0.8)/5)*5,
                            complete: false
                        },
                        {
                            name: "Smith Machine Calf Raise",
                            reps: 12,
                            sets: 5, 
                            weight: Math.ceil((squatVal*0.75)/5)*5,
                            complete: false
                        }
                    ]
                }

                plan.days.push({
                    name: name,
                    date: dayDate.format("YYYY-MM-DD"),
                    exercises: exercises
                })
            })
        }

        axios.post('/plans/add', {
            name: 'Default',
            startDate: plan.startDate,
            user: plan.user,
            days: plan.days
        }).then(res => {
            console.log(res.data)
            passUpPlan(plan)
        })
    }

    return(
        <Form onSubmit={handleSubmit}> 
            <Form.Group>
                <Form.Label>Bench Press (10RM)</Form.Label>
                <Form.Control type="text" value={benchVal} onChange={(e) => setBenchVal(e.target.value)} />
            </Form.Group>
            <Form.Group>
                <Form.Label>Squat (10RM)</Form.Label>
                <Form.Control type="text" value={squatVal} onChange={(e) => setSquatVal(e.target.value)} />
            </Form.Group>
            <Form.Group>
                <Form.Label>Plan Start Date</Form.Label>
                <Form.Control type="date" value={date} onChange={(e) => setDate(e.target.value)} />
            </Form.Group>
            <Form.Group>
                <Form.Label>Days of Week (Select 4)</Form.Label>
                <Form.Check type="checkbox" label="Sunday" value='0' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Monday" value='1' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Tuesday" value='2' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Wednesday" value='3' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Thursday" value='4' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Friday" value='5' onClick={(e) => handleCheckToggle(e.target.value)} />
                <Form.Check type="checkbox" label="Saturday" value='6' onClick={(e) => handleCheckToggle(e.target.value)} />
            </Form.Group>
            <Button type="submit">Make My Plan!</Button>
        </Form>
    )
}

export default PlanForm
